                require_once "../../Controller/connexion.php"; // Ensure the correct path

                try {
                    // Fetch all ingredients from the database
                    $sql = "SELECT * FROM ingredient";  // Modify the table name if needed
                    $stmt = $conn->prepare($sql);
                    $stmt->execute();

                    // Check if there are any ingredients
                    if ($stmt->rowCount() > 0) {
                        // Make the table responsive
                        echo '<div class="table-responsive">';
                        echo '<table class="table table-striped">';
                        echo '<thead>';
                        echo '<tr>';
                        echo '<th>#</th>';
                        echo '<th>Nom</th>';
                        echo '<th>Unite</th>';
                        echo '<th>Category</th>';
                        echo '<th>Actions</th>';
                        echo '</tr>';
                        echo '</thead>';
                        echo '<tbody>';

                        // Output each ingredient
                        while ($row = $stmt->fetch(PDO::FETCH_ASSOC)) {
                            $id_ingredient = $row['ID_ingredient'] ?? 'N/A'; 
                            $nom = $row['nom'] ?? 'No Name'; 
                            $unite = $row['unite'] ?? 'No Unit';
                            $categorie = $row['Categorie'] ?? 'N/A';

                            echo '<tr>';
                            echo '<td>' . htmlspecialchars($id_ingredient) . '</td>';
                            echo '<td>' . htmlspecialchars($nom) . '</td>';
                            echo '<td>' . htmlspecialchars($unite) . '</td>';
                            echo '<td>' . htmlspecialchars($categorie) . '</td>';
                            echo '<td>
                                    <a href="update_ingredient.php?id_ingredient=' . $id_ingredient . '" class="btn btn-info btn-sm">Edit</a>
                                    <form action="../../Controller/delete_ingredient.php" method="POST" style="display:inline;">
                                        <input type="hidden" name="id_ingredient" value="' . $id_ingredient . '">
                                        <button type="submit" class="btn btn-danger btn-sm" onclick="return confirm(\'Are you sure you want to delete this ingredient?\')">Delete</button>
                                    </form>
                                  </td>';
                            echo '</tr>';
                        }

                        echo '</tbody>';
                        echo '</table>';
                        echo '</div>'; // Close the table-responsive div
                    } else {
                        echo '<p>No ingredients found.</p>';
                    }
                } catch (PDOException $e) {
                    echo 'Error: ' . $e->getMessage();
                }
            ?>
